    private void btnConsultarProductosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConsultarProductosActionPerformed
        ProductoServices objServices = new ProductoServices();
        List<Producto> lstProductos = objServices.listarProductos();
        cargar_tabla(lstProductos);
    }//GEN-LAST:event_btnConsultarProductosActionPerformed
    public void cargar_tabla(List<Producto> lstProductos) {
        Object matriz[][] = new Object[lstProductos.size()][4];

        for (int i = 0; i < lstProductos.size(); i++) {
            matriz[i][0] = lstProductos.get(i).getCodigo();
            matriz[i][1] = lstProductos.get(i).getNombre();
            matriz[i][2] = lstProductos.get(i).getValor_inicial();
            matriz[i][3] = lstProductos.get(i).getEstado();
        }
        jTable1.setModel(new javax.swing.table.DefaultTableModel(
                matriz, new String[]{"Código", "Nombre", "Valor inicial", "Estado"}));
    }

