    private void btnProductosSubastActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProductosSubastActionPerformed
        ProductoServices objProductoServices = new ProductoServices();
        List<Producto> lstProductos = objProductoServices.ListarProductos("Subastado");
        cargar_tabla2(lstProductos);
    }//GEN-LAST:event_btnProductosSubastActionPerformed

    private void btnProductosNoSubastActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProductosNoSubastActionPerformed
        ProductoServices objProductoServices = new ProductoServices();
        List<Producto> lstProductos = objProductoServices.ListarProductos("En espera");
        cargar_tabla4(lstProductos);
    }//GEN-LAST:event_btnProductosNoSubastActionPerformed
    public void cargar_tabla2(List<Producto> lstProductos) {
        Object matriz[][] = new Object[lstProductos.size()][4];

        for (int i = 0; i < lstProductos.size(); i++) {
            matriz[i][0] = lstProductos.get(i).getCodigo();
            matriz[i][1] = lstProductos.get(i).getNombre();
            matriz[i][2] = lstProductos.get(i).getValor_inicial();
            matriz[i][3] = lstProductos.get(i).getEstado();
        }
        jTable2.setModel(new javax.swing.table.DefaultTableModel(
                matriz, new String[]{"C贸digo", "Nombre", "Valor inicial", "Estado"}));
    }
    public void cargar_tabla4(List<Producto> lstProductos) {
        Object matriz[][] = new Object[lstProductos.size()][4];

        for (int i = 0; i < lstProductos.size(); i++) {
            matriz[i][0] = lstProductos.get(i).getCodigo();
            matriz[i][1] = lstProductos.get(i).getNombre();
            matriz[i][2] = lstProductos.get(i).getValor_inicial();
            matriz[i][3] = lstProductos.get(i).getEstado();
        }
        jTable4.setModel(new javax.swing.table.DefaultTableModel(
                matriz, new String[]{"C贸digo", "Nombre", "Valor inicial", "Estado"}));
    }


